#!/bin/bash

# Base64 encoded PNG icons for Code Drill
# These are pre-generated 16x16, 32x32, 48x48, and 128x128 icons

# 16x16 icon
echo "" | base64 -d > icon-16.png

# 32x32 icon  
echo "" | base64 -d > icon-32.png

# 48x48 icon
echo "" | base64 -d > icon-48.png

# 128x128 icon
echo "" | base64 -d > icon-128.png

echo "Icons generated successfully!"
ls -la *.png